import { FC } from 'react';
import Geometry from '../utils/Geometry';
import "./LineView.css"

type LineViewProps = {
    x1: number,
    y1: number,
    x2: number,
    y2: number
}

const LineView: FC<LineViewProps> = ({ x1, y1, x2, y2 }) => {
    return (
        <div className='line' style={{
            left: ((x1 + x2) / 2) - (Geometry.pointDistance(x1, y1, x2, y2) / 2),
            top: ((y1 + y2) / 2),
            width: Geometry.pointDistance(x1, y1, x2, y2),
            transform: `rotate(${20}deg)`
        }}>
        </div>
    );
}

export default LineView;
