import React, { FC } from 'react';
import "./LineView.css"

type LineViewProps = {
    x1: number,
    y1: number,
    x2: number,
    y2: number
}

const LineView: FC<LineViewProps> = ({ x1, y1, x2, y2 }) => {
    return (
        <div className='line' style={{
            left: ((clickedPost_X + mouse_X) / 2) - (Geometry.pointDistance(clickedPost_X, clickedPost_Y, mouse_X, mouse_Y) / 2),
            top: 0,
            width: 200,
            transform: `rotate(${20}deg)`
        }}>
        </div>
    );
}

export default LineView;
