import React, { FC } from 'react';
import "./Post.css"
import Post from '../models/Post';

type PostViewProps = {
    post: Post,
    handleClick: (post: Post) => void
}

const PostView: FC<PostViewProps> = ({ post, handleClick }) => {
    const { user, date, message, color, x, y } = post;

    const formatDate = (): string => {
        const dateObj = new Date(date);
        const month = dateObj.getUTCMonth() + 1; //months from 1-12
        const day = dateObj.getUTCDate();
        const year = dateObj.getUTCFullYear();
        const time = dateObj.toLocaleString('en-US', { hour: 'numeric', minute: 'numeric', hour12: true })

        const newDate = new Date();
        const shouldShowYear = newDate.getUTCFullYear() !== year ? true : false;

        if (shouldShowYear) {
            return month + "/" + day + '/' + year + ' ' + time;
        }
        return month + "/" + day + ' ' + time;
    }

    return (
        <div className='post-container' style={{ left: x, top: y }}>
            <div
                className='post'
                style={{ backgroundColor: color }}
                onClick={() => handleClick(post)}>
                <div className='heading'>
                    <b>{user}</b> {formatDate()}
                </div>
                <div className='body'>{message}</div>
            </div>
        </div>
    );
}

export default PostView;
