import React from 'react';
import './App.css';
import BoardState from './BoardState'

type Post = {
  id: number
}

type AppProps = {}

type AppState = {
  posts: Post[],
  boardState: BoardState,
  postClickedId: number,
  postColor: string,
  newPost_X: number,
  newPost_Y: number,
}

export default class App extends React.Component<AppProps, AppState> {
  state: AppState = {
    posts: [],
    boardState: BoardState.Reading,
    postClickedId: 0,
    postColor: '',
    newPost_X: 0,
    newPost_Y: 0
  }

  postClicked = (id: number) => {
    this.setState({
        boardState: BoardState.SelectingLocation,
        postClickedId: id
    });
  }

  presentPostCreateForm = (x: number, y: number) => {
    this.setState({
      boardState: BoardState.WritingPost,
      newPost_X: x,
      newPost_Y: y
    });
  }

  render() {
    return <div>
    <div className="ui-overlay">
      <div className="header">
        <h1>The Big Bulletin Board</h1>
        <div className="header-bar" />
      </div>
    </div>
  </div>
  }
}
