import './App.css';
import BoardState from './enums/BoardState'

type Post = {
  id: number
}

type AppProps = {}

type AppState = {
  posts: Post[],
  boardState: BoardState,
  postClickedId: number,
  postColor: string,
  newPost_X: number,
  newPost_Y: number,
}

export default class App extends React.Component<AppProps, AppState> {
  state: AppState = {
    posts: [],
    boardState: BoardState.Reading,
    postClickedId: 0,
    postColor: '',
    newPost_X: 0,
    newPost_Y: 0
  }

  componentDidMount() {
