import React, { Component } from 'react';
import './App.css';
import BoardState from './enums/BoardState';
import Post from './models/Post';
import Board from './components/Board';
import PostCreateForm from './components/PostCreateForm';
import samplePosts from './SamplePosts';
import Color from './utils/Color';

type AppState = {
  posts: Post[],
  boardState: BoardState,
  postClickedId: number,
  postColor: string,
  newPost_X: number,
  newPost_Y: number,
}

export default class App extends Component {

  state: AppState = {
    posts: samplePosts,
    boardState: BoardState.Reading,
    postClickedId: 0,
    postColor: Color.randomLightColor(),
    newPost_X: 0,
    newPost_Y: 0
  }

  componentDidMount() {
    // this.getAllPosts();
  }

  getAllPosts = () => {
    // postAPI.get()
    //   .then(json => {
    //     this.setState({ posts: json })
    //   })
    //   .catch(err => {
    //     console.log('err', err);
    //   })
  }

  postClicked = (id: number) => {
    this.setState({
      boardState: BoardState.SelectingLocation,
      postClickedId: id
    });
  }

  presentPostCreateForm = (x: number, y: number) => {
    this.setState({
      boardState: BoardState.WritingPost,
      newPost_X: x,
      newPost_Y: y
    });
  }

  createPostSubitted = (message: string) => {
    let newPost: Post = {
      id: this.state.posts.length,
      parentPostId: this.state.postClickedId,
      user: 'Myself',
      date: Date.now(),
      message: message,
      color: this.state.postColor,
      x: this.state.newPost_X - 80,
      y: this.state.newPost_Y - 30,
    }

    let posts = this.state.posts;
    posts.push(newPost);
    this.setState({
      posts: posts,
      boardState: BoardState.Reading,
    })

    // postAPI.create(newPost).then(post => {
    //   console.log(post);
    // });

    // this.getAllPosts();
  }

  cancelPost = () => {
    this.setState({ boardState: BoardState.Reading })
  }

  render() {
    return <div>
      <Board
        boardState={this.state.boardState}
        posts={this.state.posts}
        postClicked={this.postClicked}
        postCreate={this.presentPostCreateForm}
        postColor={this.state.postColor}
      />
      <div className="ui-overlay">
        <div>
          <h1>The Big Bulletin Board</h1>
          <div className="header-bar" />
        </div>
        {this.state.boardState === BoardState.WritingPost && (
          <div className='comment-form-container'>
            <PostCreateForm
              onSubmit={this.createPostSubitted}
              onCancel={this.cancelPost}
              color={this.state.postColor}
            />
          </div>)
        }
      </div>
    </div>
  }
}
