import { FC } from 'react';
import Geometry from '../utils/Geometry';
import "./LineView.css"

type LineViewProps = {
    x1: number,
    y1: number,
    x2: number,
    y2: number
}

const LineView: FC<LineViewProps> = ({ x1, y1, x2, y2 }) => {

    const distance = Geometry.pointDistance(x1, y1, x2, y2);

    const lineLeft = (): number => {
        return ((x1 + x2) / 2) - (distance / 2);
    }

    const lineTop = (): number => {
        return (y1 + y2) / 2
    }

    return (
        <div className='line' style={{
            left: lineLeft(),
            top: lineTop(),
            width: distance,
            transform: `rotate(${Geometry.pointAngleDegrees(x1, y1, x2, y2)}deg)`
        }}>
        </div>
    );
}

export default LineView;
